mapper.select:
SELECT exists(
			SELECT *
	  		FROM like_movie
			WHERE like_movie_code = #{info_movie_code}
		 		AND like_id = #{member_id}
		 	LIMIT 1
		)

mapper.insert:
INSERT INTO like_movie
	VALUES(
		 CONCAT(
		 	#{info_movie_code}
		 	,"_"
		 	,#{member_id}
	 		)
		, #{info_movie_code}
		, #{member_id}
		, now()			
	)

mapper.delete:
DELETE
FROM 	like_movie
WHERE 	like_movie_code = #{info_movie_code}
 	AND like_id = #{member_id}
